events.inlineCode:
//init
window.Global_Variables = {}
window.Global_Variables.switch_scene = {"switch":false,"name":""}
window.Global_Variables.client_id = ""
//
var net = require("net")
window.Global_Variables.client = new net.Socket()
window.Global_Variables.client.connect(3008 , 'localhost')
window.Global_Variables.client.write("REGNEWID")


var waiting_recv_client_id = function(data){
    window.Global_Variables.client_id = data.toString().replace(/\n/g,"")
    window.Global_Variables.client.off("data",waiting_recv_client_id)
    window.Global_Variables.switch_scene.switch = true;
    window.Global_Variables.switch_scene.name = "Game Room"    
}



window.Global_Variables.client.on("data",waiting_recv_client_id)

console.log("Game Room Loaded")
window.Global_Variables.HP = 20
window.Global_Variables.MP = 20

window.Global_Variables.DefaultBarSize = runtimeScene.getObjects("RedBar")[0].getWidth()

window.Global_Variables.setHP = function(hp){
    var hp_obj = runtimeScene.getObjects("RedBar")[0]
    hp_obj.setWidth((window.Global_Variables.DefaultBarSize/20)*hp)
}
window.Global_Variables.setMP = function(mp){
    var mp_obj = runtimeScene.getObjects("BlueBar")[0]
    mp_obj.setWidth((window.Global_Variables.DefaultBarSize/20)*mp)
}

var adjust_MP = function(){
    if (window.Global_Variables.MP < 20){
        window.Global_Variables.MP+=1
        window.Global_Variables.setMP(window.Global_Variables.MP)
    }
}

setInterval(adjust_MP,10000)

window.Global_Variables.players_obj = {}

window.Global_Variables.send_player_position = function(client){
    var player = runtimeScene.getObjects("Player")[0];
    client.write("set_position;"+window.Global_Variables.client_id+";"+player.getX()+";"+player.getY())
}

window.Global_Variables.damage = function(x,y){
    window.Global_Variables.HP-=4
    window.Global_Variables.setHP(window.Global_Variables.HP)
    var obj = runtimeScene.createObject("Explosion")
    obj.setPosition(x-obj.getWidth()/2,y-obj.getHeight()/2)
    setTimeout(function(){obj.deleteFromScene(runtimeScene)},1000)
}


window.Global_Variables.new_player_join = function(player_id){
    var target_obj = runtimeScene.createObject("Other_Player")
    var length = runtimeScene.getObjects.length
    console.log(player_id)
    window.Global_Variables.players_obj[player_id] = target_obj
    target_obj.setPosition(900,900)
}

window.Global_Variables.attack = function(){
    if (window.Global_Variables.MP >= 2){
        window.Global_Variables.MP -= 2
        window.Global_Variables.setMP(window.Global_Variables.MP)
        var obj = runtimeScene.createObject("Explosion")
        var player = runtimeScene.getObjects("Player")[0]
        /** @type {gdjs.PlatformerObjectRuntimeBehavior} */
        var behavior = player.getBehavior("PlatformerObject")

        obj.setPosition(player.x-obj.getWidth()/2,player.y-obj.getHeight()/2)
        behavior._canJump = false
        behavior.setMaxSpeed(0)
        window.Global_Variables.client.write("attack;"+window.Global_Variables.client_id+";0;0")
        var handler = function(){
            behavior._canJump = true
            behavior.setMaxSpeed(250)
            obj.deleteFromScene(runtimeScene)
        }
        setTimeout(handler,1000)
    }
}

window.Global_Variables.recv_player_position = function(cmd){
    console.log(cmd)
    var current_player_data = JSON.parse(cmd.split(";")[1])

    if (current_player_data.id != window.Global_Variables.client_id){
        //console.log(window.Global_Variables.players_obj[current_player_data.id])
        console.log(typeof(current_player_data.x))
        console.log(typeof(current_player_data.y))
        window.Global_Variables.players_obj[current_player_data.id].setPosition(Number(current_player_data.x),Number(current_player_data.y))
    }
}

window.Global_Variables.client.on("data",function(data){
    console.log("loop!")
    var cmds = data.toString().split("\n")
    for (var i=0;i<cmds.length;i++){
        if (cmds[i] == "ERR"){
            client.destroy()
            runtimeScene.requestChange(gdjs.RuntimeScene.PUSH_SCENE, "Global");
        }else if(cmds[i].split(";")[0] == "set_player_position"){
            window.Global_Variables.recv_player_position(cmds[i])
        }else if(cmds[i].split(";")[0] == "create_player"){
            window.Global_Variables.new_player_join(cmds[i].split(";")[1])
        }else if(cmds[i].split(";")[0] == "damage"){
            window.Global_Variables.damage(Number(cmds[i].split(";")[1]),Number(cmds[i].split(";")[2]))
        }
    }
})
